    //     key: pet.id,
    //     text: pet.name,
    //     value: pet.id,
    //   }));

    const { activeItemHome, handleItemClickHome } = this.props;
    return (
      <Grid>
        <Grid.Column width={4}>
          <Menu fluid vertical tabular>
            <Menu.Item
              name="Quick Check In"
              active={activeItemHome === "Quick Check In"}
              onClick={handleItemClickHome}
            />
            <Menu.Item
              name="Checked In"
              active={activeItemHome === "Checked In"}
              onClick={handleItemClickHome}
            />
          </Menu>
        </Grid.Column>

        <Grid.Column stretched width={12}>
          <Segment>
            {activeItemHome === "Checked In" && <CheckedIn />}
            {activeItemHome === "Quick Check In" && <CheckInForm />}
          </Segment>
        </Grid.Column>
      </Grid>
    );
  }
}

export default withRouter(Home);

