import React, { Component } from "react";
import { Card, Button } from "semantic-ui-react";

class History extends Component {
  render() {
    const { pets, services } = this.props;
    const data = [];

    if (pets.length > 0) {
      pets.map((pet) => {
        if (pet.appointments.length > 0) {
          pet.appointments.map((appointment) => {
            pet.services.map((service) => {
              data.push({
                service: appointment.date,
              });
            });
          });
        }
      });
    }

    console.log(data);
    return (
      <Card.Group>
        {this.props.pets.length > 0 ? (
          this.props.pets.forEach((pet) => (
            <Card>
              {console.log("forEach pet: ->>>>>", pet)}
              <Card.Content>
                <Card.Header>{pet.name}</Card.Header>
                <div>
                  {pet.appointments.forEach((info) => (
                    <div>
                      {console.log("forEach appointment: ->>>>", info)}
                      {pet.services.map((s) => (
                        <div>
                          <Card.Meta>Date {info.date}</Card.Meta>
                          <Card.Meta>Service: {s.name}</Card.Meta>
                        </div>
                      ))}
                    </div>
                  ))}
                </div>
              </Card.Content>
              <Card.Content extra>
                <div className="ui two buttons">
                  <Button basic color="green">
                    Approve
                  </Button>
                  <Button basic color="red">
                    Decline
                  </Button>
                </div>
              </Card.Content>
            </Card>
          ))
        ) : (
          <Card>
            <Card.Content>
              <Card.Header>No Pets</Card.Header>
            </Card.Content>
          </Card>
        )}
      </Card.Group>
    );
  }
}

export default History;

