import React, { Component } from "react";
import { Form } from "semantic-ui-react";

class AddPet extends Component {
  render() {
    const {
      petInfo,
      submitingPet,
      breeds,
      petInfoInputChange,
      petBreedOnChange,
    } = this.props;
    return (
      <div
        style={{
          margin: "5%",
        }}
      >
        <Form onSubmit={submitingPet}>
          <h1>Customer Info</h1>
          <Form.Group unstackable widths={2}>
            <Form.Input
              label="Pets Name"
              placeholder="Name"
              id="name"
              name="name"
              value={petInfo.name}
              onChange={petInfoInputChange}
            />
            <Form.Input
              label="Color"
              placeholder="Color"
              onChange={petInfoInputChange}
              id="color"
              name="color"
              value={petInfo.color}
            />
            <select class="ui dropdown">
              <option value="">Breed</option>
              {breeds.map((breed) => (
                <option
                  onClick={() => petBreedOnChange(breed)}
                  id={breed.id}
                  key={breed.id}
                >
                  {breed.name}
                </option>
              ))}
            </select>
          </Form.Group>
          <Form.Group widths={2}>
            <Form.Input
              label="Special Concerns"
              placeholder="Special Concerns"
              name="specialconcerns"
              onChange={petInfoInputChange}
              value={petInfo.specialconcerns}
              id="specialconcerns"
              type="text"
            />
            <div class="field">
              <label>Short Text</label>
              <textarea rows="2"></textarea>
            </div>
            <Form.Input label="Rabies" placeholder="(xxx)xxx-xxxx" />
          </Form.Group>
          <button class="ui button">Submit</button>
        </Form>
      </div>
    );
  }
}

export default AddPet;

