import React, { Component } from "react";
import { Form, Dropdown } from "semantic-ui-react";
import { Link } from "react-router-dom";

class AddPet extends Component {
  render() {
    const {
      petInfo,
      submitingPet,
      breeds,
      petInfoInputChange,
      petBreedOnChange,
    } = this.props;

    const breedOption =
      breeds.length > 0 &&
      breeds.map((breed) => ({
        key: breed.id,
        text: breed.name,
        value: breed.id,
      }));
    return (
      <div>
        <Form onSubmit={submitingPet}>
          <h1>Customer Info</h1>
          <Form.Group unstackable widths={2}>
            <Form.Input
              label="Pets Name"
              placeholder="Name"
              id="name"
              name="name"
              value={petInfo.name}
              onChange={petInfoInputChange}
            />
            <Form.Input
              label="Color"
              placeholder="Color"
              onChange={petInfoInputChange}
              id="color"
              name="color"
              value={petInfo.color}
            />
            <Dropdown
              placeholder="Breed"
              options={breedOption}
              search
              selection
              fluid
              allowAdditions
              onChange={petBreedOnChange}
            />
          </Form.Group>
          <Form.Group widths={2}>
            <div class="field">
              <label>Special Concerns</label>
              <textarea
                rows="2"
                name="specialconcerns"
                onChange={petInfoInputChange}
                value={petInfo.specialconcerns}
                id="specialconcerns"
              ></textarea>
            </div>
            <Form.Input
              type="date"
              label="Rabies"
              placeholder="mm/dd/yyyy"
              name="rabies"
              onChange={petInfoInputChange}
              value={petInfo.rabies}
            />
          </Form.Group>
          <button class="ui button">Submit</button>
          <Link to="/client">
            <button className="back-button ui button">Back</button>
          </Link>
        </Form>
      </div>
    );
  }
}

export default AddPet;

