import React, { Component } from "react";
import { Dropdown, Search } from "semantic-ui-react";

const { services, servicesOnClick, clients } = this.props;

const initialState = { isLoading: false, results: [], value: "" };

class Home extends Component {
  render() {
    console.log(this.props.services);
    const { servicesOnClick } = this.props;

    const serviceOption =
      services.length > 0 &&
      services.map((service) => ({
        key: service.id,
        text: service.name,
        value: service.id,
      }));

    const clientSource =
      clients.length > 0 &&
      clients.map((client) => ({
        Name: client.firstname,
      }));
    handleResultSelect = (e, { result }) =>
      this.setState({ value: result.title });

    handleSearchChange = (e, { value }) => {
      this.setState({ isLoading: true, value });

      setTimeout(() => {
        if (this.state.value.length < 1) return this.setState(initialState);

        const re = new RegExp(_.escapeRegExp(this.state.value), "i");
        const isMatch = (result) => re.test(result.title);

        this.setState({
          isLoading: false,
          results: _.filter(clientSource, isMatch),
        });
      }, 300);
    };
    return (
      <div>
        <h3 className="ui header">Dashboard</h3>
        <div className="ui grid">
          <div className="four wide column">
            <div className="ui vertical fluid tabular menu">
              <a className="active item">Check In</a>
              <a className="item">Scheduled Appointments</a>
            </div>
          </div>
          <div className="dash twelve wide stretched column">
            <div className="ui segment">
              <div className="ui input focus">
                <Search
                  loading={isLoading}
                  onResultSelect={this.handleResultSelect}
                  onSearchChange={_.debounce(this.handleSearchChange, 500, {
                    leading: true,
                  })}
                  results={results}
                  value={value}
                  {...this.props}
                />
                <Dropdown
                  placeholder="Service"
                  options={serviceOption}
                  search
                  selection
                  fluid
                  allowAdditions
                  onChange={servicesOnClick}
                />
              </div>
              <button className="ui secondary button">Check In</button>
              <button className="ui button">Cancel</button>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default Home;

