import React, { Component } from "react";
import { Card, Button } from "semantic-ui-react";

class History extends Component {
  render() {
    const { pets, services } = this.props;
    const data = [];

    if (pets.length > 0) {
      pets.forEach((p) => {
        p.appointment.forEach((a) => {
          services.forEach((s) => {
            if (s.id === a.service_id)
              data.push({
                serviceName: a.name,
                date: p.date,
              });
          });
        });
      });
    }

    console.log("history props", this.props);
    console.log(data);
    return (
      <Card.Group>
        {this.props.pets.length > 0 ? (
          this.props.pets.map((pet) => (
            <Card>
              <Card.Content>
                <Card.Header>{pet.name}</Card.Header>
                <Card.Meta>Pet</Card.Meta>
                {pet.services.map((service) => (
                  <Card>
                    <Card.Description>
                      <strong>Service Type: {service.name}</strong>
                    </Card.Description>
                    <Card.Description></Card.Description>
                  </Card>
                ))}
              </Card.Content>
              {/* <Card.Content extra>
                <div className="ui two buttons">
                  <Button basic color="green">
                    Approve
                  </Button>
                  <Button basic color="red">
                    Decline
                  </Button>
                </div>
              </Card.Content> */}
            </Card>
          ))
        ) : (
          <Card>
            <Card.Content>
              <Card.Header>No Pets</Card.Header>
            </Card.Content>
          </Card>
        )}
      </Card.Group>
    );
  }
}

export default History;

